async function addBackbones (object_to_render, config) {
    let backbones = new Object3D();
    backbones.name = "backbones";

    let coords_W = [];
    let coords_C = [];

    for (let i = 0; i < config.sequence.length-1; i++) {
        let R = new Matrix4().copy(config.baseFramesWatson[i]);
        let v = (config.sequence[i] === 'C' || config.sequence[i] === 'T') ?
                    new Vector3().fromArray([-5.0, 8.0, 2.0]) :
                    new Vector3().fromArray([-5.0, 8.0, 2.0]);
        let groove = new Matrix4().setPosition(v);
        coords_W.push(new Vector3().setFromMatrixPosition(R.clone().multiply(groove)));
    }

    for (let i = 1; i < config.sequence.length; i++) {
        let R = new Matrix4().copy(config.baseFramesCrick[i]);
        let v = (config.sequence[i] === 'C' || config.sequence[i] === 'T') ?
                    new Vector3().fromArray([-5.0, -8.0, -2.0]) :
                    new Vector3().fromArray([-5.0, -8.0, -2.0]);
        let groove = new Matrix4().setPosition(v);
        coords_C.push(new Vector3().setFromMatrixPosition(R.clone().multiply(groove)));
    }

    //make splines:
    let spline_W = new CatmullRomCurve3(coords_W);
    let spline_C = new CatmullRomCurve3(coords_C);

    let geometry_W = new TubeBufferGeometry( spline_W, config.sequence.length*4, 0.3, 8, false );
    let geometry_C = new TubeBufferGeometry( spline_C, config.sequence.length*4, 0.3, 8, false );

    //let backboneGeom = BufferGeometryUtils.mergeBufferGeometries([geometry_W, geometry_C], false);
    //let backboneMesh = new THREE.Mesh(backboneGeom, new THREE.MeshToonMaterial({color: 0xbbbbbb }));
    let backboneW = new Mesh(geometry_W, new MeshToonMaterial({color: 0x5e5073 }));
    let backboneC = new Mesh(geometry_C, new MeshToonMaterial({color: 0x2c2136 }));

    backbones.add(backboneW);
    backbones.add(backboneC);

    backbones.layers.set(dnaView_camera_layer.backbones);
    backbones.traverse( function( child ) { child.layers.set( dnaView_camera_layer.backbones ) } );

    object_to_render.add(backbones);
}

