    findMaximalXRange() {
        let seq_lengths = [];
        this.svg.selectAll('.data .sequences .sequence')
                .each(function() {
                    seq_lengths.push(src_select(this).attr('seq').length);//.select('path.line').data());
                });

        return [0, src_max(seq_lengths)];
    }

    findMaximalYRange() {
        let maxes = [];
        let mins = [];
        this.svg.selectAll(`.data .${this.coords_to_show} .coords`)
                .each(function() {
                    if (!src_select(this).select('path.line').empty()) {
                        maxes.push(src_max(src_select(this).select('path.line').datum()));
                        mins.push(src_min(src_select(this).select('path.line').datum()));
                    }
                });

        return [src_min(mins), src_max(maxes)];
    }

