    clickInsideElement : function (e, className) {
        let el = e.srcElement || e.target;

        if ( el.classList.contains(className) ) {
          return el;
        } else {
          while ( el = el.parentNode ) {
            if ( el.classList && el.classList.contains(className) ) {
              return el;
            }
          }
        }

        return false;
    },

